import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movies, setMovies] = useState();

  function fetchMoviesHandler() {
    // default GET request will be send
    fetch("https://swapi.dev/api/films/") // fetch is used to make an HTTP request to the backend
      .then((response) => {
        return response.json(); // translate json to js
      })
      .then((data) => {
        const transformedMovies = data.results.map();
        setMovies(data.results); // access results
      });
  }

  return (
    <React.Fragment>
      <section>
        <button>Fetch Movies</button>
      </section>
      <section>
        <MoviesList movies={movies} />
      </section>
    </React.Fragment>
  );
}

export default App;
